Invoke-WebRequest -Uri http://localhost:8000/login -SessionVariable s -UseBasicParsing ; $token = ($s.Cookies.GetCookies("http://localhost:8000") | Where-Object { $_.Name -eq "XSRF-TOKEN" }).Value; Write-Host "XSRF-TOKEN=" $token; Invoke-WebRequest -Method POST -Uri http://localhost:8000/login -Body @{email="admin@gmail.com"; password="admin123"; _token=$token } -WebSession $s -UseBasicParsing -SessionVariable r ; Write-Host $r.StatusCode; $r.RawContent
